area = sys.argv[4]

# 以下如果看不懂直接默认就行只需改上面

# 系数K查询到天气后降低步数比率
K_dict = {"多云": 0.9, "阴": 0.8, "小雨": 0.7, "中雨": 0.5, "大雨": 0.4, "暴雨": 0.3, "大暴雨": 0.2, "特大暴雨": 0.2}

# 北京时间
time_bj = datetime.datetime.today() + datetime.timedelta(hours=8)
now = time_bj.strftime("%Y-%m-%d %H:%M:%S")
headers = {'User-Agent': 'MiFit/5.3.0 (iPhone; iOS 14.7.1; Scale/3.00)'}
CF_WORKER_URL = "https://huami.199846.dpdns.org"


# 获取区域天气情况
def getWeather():
    if area == "NO":
        print(area == "NO")
        return
    else:
        global K, type
        url = 'http://wthrcdn.etouch.cn/weather_mini?city=' + area
        hea = {'User-Agent': 'Mozilla/5.0'}
        r = requests.get(url=url, headers=hea)
        if r.status_code == 200:
            result = r.text
            res = json.loads(result)
            weather_type = res['data']['forecast'][0]['type']
            for key in K_dict:
                if key in weather_type:
                    K = K_dict[key]
                    type = weather_type
                    return
        else:
            print("获取天气情况出错")


# 获取北京时间确定随机步数&启动主函数
def getBeijinTime():
    global K, type
    K = 1.5
    type = ""
    hea = {'User-Agent': 'Mozilla/5.0'}
    url = r'https://apps.game.qq.com/CommArticle/app/reg/gdate.php'
    if open_get_weather == "True":
        getWeather()
    r = requests.get(url=url, headers=hea)
    if r.status_code == 200:
        result = r.text
        pattern = re.compile('\\d{4}-\\d{2}-\\d{2} (\\d{2}):\\d{2}:\\d{2}')
        find = re.search(pattern, result)
        hour = find.group(1)
        min_ratio = max(math.ceil((int(hour) / 3) - 1), 0)
        max_ratio = math.ceil(int(hour) / 3)
        min_1 = 4000 * min_ratio
        max_1 = 4000 * max_ratio
        min_1 = int(K * min_1)
        max_1 = int(K * max_1)
    else:
        print("获取北京时间失败")
        return
    if min_1 != 0 and max_1 != 0:
        user_mi = sys.argv[1]
        passwd_mi = sys.argv[2]
        user_list = user_mi.split('#')
        passwd_list = passwd_mi.split('#')
        if len(user_list) == len(passwd_list):
            if K != 1.0:
                msg_mi = "由于天气" + type + "，已设置降低步数,系数为" + str(K) + "。\n"
            else:
                msg_mi = ""
            for user_mi, passwd_mi in zip(user_list, passwd_list):
                msg_mi += main(user_mi, passwd_mi, min_1, max_1)
        try:
            pushUrl = "https://wxpusher.zjiecode.com/api/send/message"
            summary = now + " 刷步数通知"
            data = {
                "appToken": sys.argv[5],
                "summary": summary,
                "content": msg_mi,
                "contentType": 2,
                "uids": [sys.argv[7]]
            }
            headers_push = {"Content-Type": "application/json"}
            result = requests.post(pushUrl, data=data, headers=headers_push).text
            print(result)
        except Exception as e:
            print("推送通知失败: ", e)
    else:
        print("当前主人设置了0步数呢，本次不提交")
        return

